import React, { useState } from "react";
import "./gameStyle.css";

const winner = (cell) => {
  const line = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < line.length; i++) {
    const [a, b, c] = line[i];
    if (cell[a] && (cell[a] === cell[b]) & (cell[a] === cell[c])) {
      return cell[a];
    }
  }
  return null;
};

const cell = ["Y", null, null, "Y", "X", "X", "Y", null, null];

console.log(winner(cell));

const Tictactoe = () => {
  return (
    <div className="game__ticTacToe">
      <Board></Board>
    </div>
  );
};

//todo: ---------- Board game ---------
function Board(props) {
  const [board, seBoard] = useState(Array(9).fill(null));
  return (
    <div className="game__board">
      {board.map((item, index) => {
        const handleClickCell = () => {
         seBoard((value)=>(value))
        };
        return (
          <Cell key={index} value={index} clicked={handleClickCell}></Cell>
        );
      })}
    </div>
  );
}

//todo: ------------ cell game -------
function Cell(props) {
  return (
    <div className="game__cell" onClick={props.clicked}>
      {props.value}
    </div>
  );
}

export default Tictactoe;
