import React, { useState } from "react";
import "./gameStyle.css";

const Tictactoe = () => {
  const [board, seBoard] = useState(Array(9).fill(null));
  const handleClick = () => {
    console.log(123);
  };
  return (
    <div className="game__ticTacToe">
      <Board cells={board} onClick={handleClick}></Board>
    </div>
  );
};
//todo: ---------- Board game ---------
function Board(props) {
  return (
    <div className="game__board">
      {props.cells.map((item, index) => {
        return (
          <Cell
            key={index}
            title=""
            onClick={() => props.clicked(index)}
          ></Cell>
        );
      })}
    </div>
  );
}

//todo: ------------ cell game -------
function Cell(props) {
  return (
    <div className="game__cell" onClick={props.clicked}>
      {props.value}
    </div>
  );
}

export default Tictactoe;
