import axios from "axios";
import React, { useEffect, useRef, useState } from "react";

const getApi = async (value) => {
  const response = await axios.get(
    `https://hn.algolia.com/api/v1/search?query=${value}`
  );
  const data = response.data.hits;
  return data;
};

const Hacknews_Api = () => {
  const [titleHits, setTitleHist] = useState([]);
  const [titleValue, setTitleValue] = useState("React js");
  

  const handleData = useRef({});
  handleData.current = async () => {
    const dataHist = await getApi(titleValue);
    setTitleHist(dataHist || []);
  };
  useEffect(() => {
    handleData.current();
  }, [titleValue]);

  return (
    <div className="p-12">
      <div>
        <input
          type="text"
          name="find__value"
          id=""
          className="border px-3 py-1"
          value={titleValue}
          onChange={(e) => setTitleValue(e.target.value)}
        />
      </div>
      <div>
        {titleHits.length > 0 &&
          titleHits.map((item, index) => <p key={index}>{item.title}</p>)}
      </div>
    </div>
  );
};

export default Hacknews_Api;
