import React, { useState } from "react";
import "../../style/toggle.css";

const ToggleState = () => {
  const [on, setOn] = useState(false);
  console.log(on);
  return (
    <div>
    clg
    <h1 className="title">Chương 3: Tìm hiểu về react hook useState</h1>
      <div className="toggle">
        <div className="spinner"></div>
      </div>
      <div className="control__toggle">
        <button className="toggle__on toggle__btn" onClick={()=> setOn(true)}>On</button>
        <button className="toggle__of toggle__btn">Off</button>
      </div>
    </div>
  );
};

export default ToggleState;
