import React from "react";
import "./gameStyle.css";

const Tictactoe = () => {
  return (
    <div className="game__ticTacToe">
      <Board></Board>
    </div>
  );
};
//todo: ---------- Board game ---------
function Board() {
  return (
    <div className="game__board">
    {Array(9).fill().map((item, index) => {
      return(
        <Cell key={index} title="" onClick></Cell>
      );
    }

    )}
     
    </div>
  );
}

//todo: ------------ cell game -------
function Cell(props) {
  return <div className="game__cell" onClick={props.onClick}>{props.title}</div>;
}

export default Tictactoe;
