import React, { useState } from "react";
import "./gameStyle.css";


const winner = (cell) =>{
  const line = [
    [0,1,2],
    [3,4,5],
    [6,7,8],
    [0,3,6],
    [1,4,7],
    [2,5,8],
    [0,4,8],
    [2,4,6]
  ]
  for(let i =0; i<line.length; i++){
    const [a,b,c] = line[i];
    console.log(a,b,c);
    if(cell[a] && cell[a] === cell[b] & cell[a] === cell[c]){
      return cell[a]
    }
    
  }
  return null;
}





winner()






const Tictactoe = () => {
  const [board, seBoard] = useState(Array(9).fill(null));

  return (
    <div className="game__ticTacToe">
      <Board cells={board}></Board>
    </div>
  );
};






//todo: ---------- Board game ---------
function Board(props) {
  return (
    <div className="game__board">
      {props.cells.map((item, index) => {
        const handleClickCell = () => {
          console.log(index);
        };
        return <Cell key={index} value={index} clicked={handleClickCell}></Cell>;
      })}
    </div>
  );
}

//todo: ------------ cell game -------
function Cell(props) {
  return (
    <div className="game__cell" onClick={props.clicked}>
      {props.value}
    </div>
  );
}

export default Tictactoe;
