import React, { useState } from "react";

const ToggleState = () => {
  const [on, setOn] = useState(false);
  console.log(on);
  return (
    <div>
      <div className="toggle">
        <div className="spinner"></div>
        <div className="control__toggle">
            <button>On</button>
        </div>
      </div>
    </div>
  );
};

export default ToggleState;
