import React, { useState } from "react";

const ToggleState = () => {
  const [on, setOn] = useState(false);
  console.log(on);
  console.log(setOn);
  return (
    <div>
      <button
        className="btnToggle"
        onClick={() => {
          useState(true);
        }}
      >
        Toggle
      </button>
    </div>
  );
};

export default ToggleState;
