import axios from "axios";
import React, { useEffect, useState } from "react";
const getApi = async (value) => {
  const response = await axios.get(
    `https://hn.algolia.com/api/v1/search?query=${value}`
  );
  const data = response.data.hits;
  return data;
};

const Hacknews_Api = () => {
  const [titleHits, setTitleHist] = useState([]);
  const handleData = async () => {
    const dataHist = await getApi();
    setTitleHist(dataHist);
   
  };
  useEffect(() => {
    handleData();
  }, []);

  return <div>
    <div>
        {titleHits.length > 0 && titleHits.map((item, index) =>(
            <p key={idne}>{item.title}</p>
        ))}
    </div>
  </div>;
};

export default Hacknews_Api;
