import React, { useReducer } from "react";

//todo: 1. init state
const initState = {
  job: "",
  listJob: [],
};

//todo: 2. action
const SET_JOB = "set_job";
const ADD_JOB = "add_Job";
const setJob = (payload) => {
  return {
    type: SET_JOB,
    payload,
  };
};

//todo: 3. reducer
const reducer = (state, action) => {
    let newState;
  switch (action.type) {
    case SET_JOB:
      newState = {
        ...state,
        job: action.payload,
      };
      br
    case ADD_JOB:
      break;

    default:
      break;
  }
};
const Todo = () => {
  const [state, dispatch] = useReducer(reducer, initState);
  const { job, jobs } = state;

  return (
    <div>
      <div className="wrapper__todo">
        <div className="form__input">
          <input
            type="text"
            placeholder="Enter..."
            value={job}
            onChange={(e) => dispatch(setJob(e.target.value))}
          />
          <button className="btn todo__add">Add</button>
        </div>
        <div className="content">
          <ul>
            <li>abc</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Todo;
