import axios from "axios";
import React, { useEffect, useRef, useState } from "react";

const getApi = async (value) => {
  const response = await axios.get(
    `https://hn.algolia.com/api/v1/search?query=${value}`
  );
  const data = response.data.hits;
  return data;
};

const Hacknews_Api = () => {
  const [titleHits, setTitleHist] = useState([]);
  const [query, setQuery] = useState("React js");
  const [loading, setLoading] = useState(true);

  const handleData = useRef({});
  handleData.current = async () => {
    setLoading(true);
    try {
      const dataHist = await getApi(query);
      setTitleHist(dataHist || []);
      setLoading(false);
    } catch (error) {
        
    }
  };
  useEffect(() => {
    handleData.current();
  }, [query]);

  return (
    <div className="wrapper p-12">
      <div className="query">
        <input
          type="text"
          name="find__value"
          id=""
          className="border px-3 py-1"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
      {loading && (
        <div
          className="loading w-10 h-10 border-blue-500 rounded-full border-4 border-r-4
      border-r-transparent animate-spin text-center"
        ></div>
      )}
      <div className="content">
        {!loading &&
          titleHits.length > 0 &&
          titleHits.map((item, index) => <p key={index}>{item.title}</p>)}
      </div>
    </div>
  );
};

export default Hacknews_Api;
