import axios from "axios";
import React, { useEffect, useReducer, useRef } from "react";

// 1. init state: 0 (gia tri khoi tao = 0)
// 2. Action: up(state + 1) down(state - 1)
// 3. Reducer
// 4. Dispatch

const initState = {
  url: "https://hn.algolia.com/api/v1/search?query=css}",
  query: "",
  loading: true,
  dataShow: [],
};

const FETCHING = "fetching";
const SET_DATA = "setData";

const setData = (payload) => {
  return {
    type: SET_DATA,
    payload,
  };
};

const reducer = (state, action) => {
  let newState;
  switch (action.type) {
    case SET_DATA:
      newState = {
        ...state,
        dataShow: action.payload,
      };
      break;

    default:
      break;
  }
  return newState;
};

const getApi = async (url) => {
  const response = await axios.get(url);
  const dataApi = response.data.hits;
  return dataApi;
};

const HackNewsWithReducer = () => {
  const [state, dispatch] = useReducer(reducer, initState);
  const handleLogic = useRef({});
  handleLogic.current = async () => {
    const data = await getApi(initState.url);
    dispatch(setData(data));
    console.log(state);
  };

//   handleLogic.current();

  return (
    <div>
      <div>
        {/* {initState.dataShow.map((item, index) => {
          return <p>{item.title}</p>;
        })} */}
      </div>
    </div>
  );
};

export default HackNewsWithReducer;
