import React, { useState } from "react";

const ToggleState = () => {
  const [on, setOn] = useState(false);
  console.log(on);
  return (
    <div>
      <button
        className="btnToggle"
        onClick={() => {
          setOn(true);
        }}
      >
        Toggle {on?"ON":"ON"}
      </button>
    </div>
  );
};

export default ToggleState;
